INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'en_US', '{WEB_TITLE} - Someone has a question about your item', '<p>Hi {CONTACT_NAME}!</p>\n<p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) left you a message about your item {ITEM_TITLE}:</p>\n<p>{COMMENT}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'en_US', 'Please validate your {WEB_TITLE} account ', '<p>Hi {USER_NAME},</p>\n<p>Please validate your registration by clicking on the following link: {VALIDATION_LINK}</p>\n<p>Thank you!</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'en_US', '{WEB_TITLE} - Registration successful!', '<p>Hi {USER_NAME},</p>\n<p>You\'ve sucessfully registered for {WEB_TITLE}.</p>\n<p>Thank you!</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'en_US', 'Look at what I discovered at {WEB_TITLE}', '<p>Hi {FRIEND_NAME},</p>\n<p>Your friend {USER_NAME} wants to share this item with you:</p>\n<p>{ITEM_URL}</p>\n<p>Message:</p>\n<p>{COMMENT}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'en_US', '{WEB_TITLE} - New ads in the last hour', '<p>Hi {USER_NAME},</p>\n<p>New ads have been published in the last hour. Take a look at them:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>To unsubscribe from this alert, click on: {UNSUB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'en_US', '{WEB_TITLE} - New ads in the last day', '<p>Hi {USER_NAME},</p>\n<p>New ads have been published in the last day. Take a look at them:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>To unsubscribe from this alert, click on: {UNSUB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'en_US', '{WEB_TITLE} - New ads in the last week', '<p>Hi {USER_NAME},</p>\n<p>New ads have been published in the last week. Take a look at them:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>To unsubscribe from this alert, click on: {UNSUB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'en_US', '{WEB_TITLE} - New ad', '<p>Hi {USER_NAME},</p>\n<p>A new ad has been published, take a look at it:</p>\n<p>{ADS}</p>\n<p>-------------</p>\n<p>To unsubscribe from this alert, click on: {UNSUB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'en_US', '{WEB_TITLE} - New comment on the ad with id {ITEM_ID}', '<p>There\'s a new comment on the ad with id {ITEM_ID} <br />URL: {ITEM_URL}</p>\n<p>Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}<br />Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'en_US', '{WEB_TITLE} - Edit options for the ad with id {ITEM_ID}', '<div><p>Hi {USER_NAME},</p>\n<p>You\'re not registered at {WEB_TITLE}, but you can still edit or delete the item {ITEM_TITLE} for a short period of time.</p>\n<p>You can edit your item by following this link: {EDIT_LINK}</p>\n<p>You can delete your item by following this link: {DELETE_LINK}</p>\n\n<p>If you register as a user to post items, you will have full access to editing options.</p>\n<p>Regards,</p>\n{WEB_TITLE}</div>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'en_US', '{WEB_TITLE} - Validate your ad', '<p>Hi {USER_NAME},</p>\n<p>You\'re receiving this e-mail because an ad has been published at {WEB_TITLE}. Please validate this item by clicking on the link at the end of this e-mail. If you didn\'t publish this ad, please ignore this e-mail.</p>\n<p>Ad details:</p>\n<p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Price: {ITEM_PRICE}<br />Country: {ITEM_COUNTRY}<br />Region: {ITEM_REGION}<br />City: {ITEM_CITY}<br />Url: {ITEM_URL}<br /><br />Validate your ad: {VALIDATION_LINK}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'en_US', '{WEB_TITLE} - New item', '<div><p>Dear {WEB_TITLE} admin,</p>\n<p>You\'re receiving this email because an ad has been published at {WEB_TITLE}.</p>\n<p>Ad details:</p>\n<p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p>\n<p>{ITEM_DESCRIPTION_ALL_LANGUAGES}</p>\n<p>Price: {ITEM_PRICE}<br />Country: {ITEM_COUNTRY}<br />Region: {ITEM_REGION}<br />City: {ITEM_CITY}<br />Url: {ITEM_URL}<br /><br />You can edit this item by clicking on the following link: {EDIT_LINK}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p></div>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'en_US', '{WEB_TITLE} - Recover your password', '<p>Hi {USER_NAME},</p>\n<p>We\'ve sent this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p>\n<p>The link will be disabled in 24 hours.</p>\n<p>If you didn\'t forget your password, ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'en_US', '{WEB_TITLE} - You requested an e-mail change', '<p>Hi {USER_NAME}</p>\n<p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p>\n<p>Regards,</p>\n<p>{WEB_TITLE}</p>');
