INSERT INTO `oc_t_pages_description` (fk_i_pages_id,fk_c_locale_code,s_title,s_text) VALUES (99,'en_US','About','<p>&nbsp;</p><h1>About</h1><p><a href="http://sourceforge.net/">SourceForge</a> is dedicated to making <a href="http://opensource.org/">open source</a> projects successful.</p><p>We thrive on community collaboration to help us create the  leading resource for open source software development and distribution.  With <a href="https://sourceforge.net/create">the tools we provide</a>, 2.7&nbsp;million developers create powerful software n over <a href="https://sourceforge.net/directory/">260,000&nbsp;projects</a>. Our <a href="https://sourceforge.net/directory/">popular directory</a> connects more than 46&nbsp;million consumers with these open source projects  and serves more than 2,000,000&nbsp;downloads a day. SourceForge is where  open source happens.</p><p>SourceForge.net is owned and operated by <a href="http://geek.net/">Geeknet, Inc.</a>, a publicly traded US-based company.</p>');